import {Component} from 'react';

import CardList from './components/CardList/CardList.component';
import './App.css';

// TODO to change this from a functional component to a class component we changed function App() to
// TODO class App extends Component and then put the entire return statement inside of a render() { }
// TODO that's all we need to do to convert on a basic level
// TODO render() is a method

class App extends Component {
  constructor(props) {
    super(props);

    // this state is always a json object
    this.state = {
      monsters: [],
      searchField: '',
    }
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      // whatever is returned from the above json will be passed into the users below
      .then((users) => this.setState(() => {
        return {monsters: users}
      }
      ))
  }

  onSearchChange = (event) => {
    const searchField = event.target.value.toLocaleLowerCase()
    this.setState(() => {
      return {searchField}
    })
  }

  render() {
    // these two consts allow us to remove the 'this.state' from the state references
    const { monsters, searchField } = this.state
    const { onSearchChange } = this
    // we know we have an array of monsters [ { name: 'Kriston' }, { name: 'Mash' } ]
    const filteredMonsters = monsters.filter((monster) => {
      return monster.name.toLocaleLowerCase().includes(searchField)
    })
    return (
      <div className="App">
        <input className='search-box' type='search' placeholder='search monsters' onChange={
          onSearchChange
        } />
        <CardList monsters={filteredMonsters} />
      </div>
    );
  }
}

export default App;
