import {Component} from "react";

import logo from './logo.svg';
import './App.css';

// TODO to change this from a functional component to a class component we changed function App() to
// TODO class App extends Component and then put the entire return statement inside of a render() { }
// TODO that's all we need to do to convert on a basic level
// TODO render() is a method

class App extends Component {
  constructor(props) {
    super(props);

    // this state is always a json object
    this.state = {
      monsters: [],
    }
    console.log('constructor')
  }

  componentDidMount() {
    console.log('component did mount')
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      // whatever is returned from the above json will be passed into the users below
      .then((users) => this.setState(() => {
        return {monsters: users}
      },
      () => {
        console.log(this.state)
      }
      ))
  }

  render() {
    console.log('render')
    return (
      <div className="App">
        <input className='search-box' type='search' placeholder='search monsters' />
        {
          this.state.monsters.map((monster) => {
            return (
              <div key={monster.id}>
                <h1> {monster.name} </h1>
              </div>)
          })
        }
      </div>
    );
  }
}

export default App;
